import React from 'react';
import { Droppable } from 'react-beautiful-dnd';
import styled from "styled-components";

import './styles.css';

import ToDoCard from './ToDoCard';
import ToDoListActionButton from './ToDoListActionButton';

const ListContainer = styled.div`
    background-color: cadetblue;
    border-radius: 3px;
    width: 300px;
    padding: 8px;
    height: 100%;
    margin-right: 8px;
`

const ToDoList = ({ title, cards, listID }) => {
    return (
        <Droppable droppableId={String(listID)}>
            {provided => (
                <div {...provided.droppableProps} ref={provided.innerRef} className="to-do-list-container">
                    <h4>{title}</h4>
                    { cards.map((card, index) => (
                        <ToDoCard 
                            key={card.id} 
                            id={card.id} 
                            text={card.text} 
                            index={index}
                        />
                    )) }
                    <ToDoListActionButton listID={listID}/>
                    {provided.placeholder}
                </div>
            )}
        </Droppable>
    );
}

export default ToDoList;
