import { CONSTANTS } from './../actions';

let listID = 3;
let cardId = 3;

const initialState = [
    {
        title: "Aprovados",
        id: 0,
        cards: [
            {
                id: 0,
                text: "card 0"
            },
            {
                id: 1,
                text: "card 1"
            }
        ]
    },
    {
        title: "Em andamento",
        id: 1,
        cards: [
            {
                id: 0,
                text: "card 0"
            },
            {
                id: 1,
                text: "card 1"
            },
            {
                id: 2,
                text: "card 2"
            }
        ]
    },
    {
        title: "Concluído",
        id: 2,
        cards: [
            {
                id: 0,
                text: "card 0"
            },
            {
                id: 1,
                text: "card 1"
            },
            {
                id: 2,
                text: "card 2"
            }
        ]
    }
];

const listsReducer = (state = initialState, action) => {
    switch(action.type) {
        case CONSTANTS.ADD_LIST:
            const newList = {
                title: action.payload,
                cards: [],
                id: listID
            }
            listID+=1;
            return [...state, newList];
        case CONSTANTS.ADD_CARD:
            const newCard = {
                text: action.payload,
                id: cardId
            }
            cardId+=1
        default:
            return state;
    }
}

export default listsReducer;
